	//Record score
	public static void scoreIncrement() {
		scoreCount++;
		gameScore.setText("Score: " + scoreCount);
	}
	
	//Function for setting color scheme
	private static void registerHandler(Shape s, Color defaultColor, Color hoverColor) {
        s.setOnMouseEntered( e -> s.setFill(hoverColor));
        s.setOnMouseExited(e -> s.setFill(defaultColor));
    }
	//Separate method to load quiz screen
	private void loadQuiz(String title) {
		Parent Game;
		try {
			Game = FXMLLoader.load(getClass().getResource("QuizScreen.fxml"));
			Scene root = new Scene(Game);
	    	Stage stage = new Stage();
	    	
	    	stage.initModality(Modality.WINDOW_MODAL);
	    	stage.setTitle(title);
	    	stage.setScene(root);
	    	stage.show();
		} catch (IOException e1) {
			// TODO Auto-generated catch block
			e1.printStackTrace();
		}
      }
	//Change state color on win
	public static void changeStateColorWin() {
		selectedState.setFill(Color.BLUE);
		registerHandler(selectedState, Color.BLUE, Color.BLUE);
	}
	//Change state color on lose
	public static void changeStateColorLose() {
		selectedState.setFill(Color.RED);
		registerHandler(selectedState, Color.RED, Color.RED);
	}
	
	public static Boolean isClickable(SVGPath path) {
		if(path.getFill().equals(Color.BLUE) || path.getFill().equals(Color.RED)) {
			return false;
		} else {
			return true;
		}
	}
